const PrivateKey    = process.env.REACT_APP_PRIVATE_KEY;
const RpcHttpUrl    = process.env.REACT_APP_RPC_HTTP_URL;
//create web3 connection
const web3          = new Web3(new Web3.providers.HttpProvider(RpcHttpUrl));  

